import React, { useState } from 'react';
import axios from 'axios';
import '../style.css'; // Import CSS styles

const Post = ({ post, fetchPosts }) => {
    const [isLiking, setIsLiking] = useState(false);  // State to handle like button disabling
    const [comment, setComment] = useState('');       // State to handle comment input
    const [error, setError] = useState('');           // State for error handling

    const handleLike = async () => {
        setIsLiking(true);  // Disable the like button while the request is being made
        try {
            await axios.post(`http://localhost:5000/api/posts/${post._id}/like`, {}, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Post liked');
            fetchPosts(); // Refresh the post list after liking
        } catch (error) {
            console.error('Error liking post:', error);
        } finally {
            setIsLiking(false);  // Re-enable the like button
        }
    };

    const handleComment = async () => {
        if (!comment.trim()) return; // Prevent submitting empty comments
        try {
            await axios.post(`http://localhost:5000/api/posts/${post._id}/comment`, { content: comment }, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Comment added');
            fetchPosts();  // Refresh the post list after adding a comment
            setComment(''); // Clear the comment input after submission
        } catch (error) {
            console.error('Error adding comment:', error);
            setError('Failed to add comment. Please try again.'); // Set error state
        }
    };

    return (
        <div className="card mb-3 post-container shadow-sm">
            <div className="card-body">
                <h5 className="card-title">{post.content}</h5>
                {post.image && <img src={post.image} alt="Post" className="img-fluid post-image" />} {/* Post image */}

                <div className="d-flex align-items-center mt-2">
                    <button onClick={handleLike} disabled={isLiking} className="btn btn-outline-primary me-2">
                        {isLiking ? 'Liking...' : 'Like'}
                    </button>
                    <span className="me-3">{post.likes.length} Likes</span> {/* Display number of likes */}
                </div>

                {/* Comment input */}
                <div className="comment-section mt-3">
                    <input 
                        type="text" 
                        className="form-control" 
                        placeholder="Add a comment..." 
                        value={comment} 
                        onChange={(e) => setComment(e.target.value)} 
                        onKeyDown={(e) => {
                            if (e.key === 'Enter') {
                                handleComment(); // Call the function to submit comment
                            }
                        }} 
                    />
                    <button onClick={handleComment} className="btn btn-primary mt-2">Submit</button>
                </div>

                {/* Error message */}
                {error && <p className="text-danger">{error}</p>}

                {/* Display comments */}
                <div className="comments-list mt-3">
                    {post.comments.map(comment => (
                        <div key={comment._id} className="comment mb-2">
                            <p className="m-0">{comment.content}</p> {/* Ensure each comment has a unique key */}
                        </div>
                    ))}
                </div>
                <span>{post.comments.length} Comments</span> {/* Display number of comments */}
            </div>
        </div>
    );
};

export default Post;
