// src/components/UserProfile.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Post from './Post';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css'; // Ensure Bootstrap is imported

const UserProfile = () => {
    const [user, setUser] = useState(null);
    const [posts, setPosts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/users/profile', {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });
                setUser(response.data);

                if (response.data._id) {
                    const postsResponse = await axios.get(`http://localhost:5000/api/posts/user/${response.data._id}`, {
                        headers: {
                            'Authorization': `Bearer ${localStorage.getItem('token')}`
                        }
                    });
                    setPosts(postsResponse.data);
                } else {
                    console.error('User ID is undefined');
                }
            } catch (error) {
                console.error('Error fetching user profile:', error);
            }
        };

        fetchUserProfile();
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    if (!user) return <p>Loading...</p>;

    return (
        <div className="container mt-4">
            <div className="row">
                <div className="col-md-4">
                    <div className="card">
                        <img src={user.profilePicture} alt="Profile" className="card-img-top" />
                        <div className="card-body">
                            <h5 className="card-title">{user.username}</h5>
                            <p className="card-text">{user.bio}</p>
                            <h6>Followers: {user.followers ? user.followers.length : 0}</h6>
                            <h6>Following: {user.following ? user.following.length : 0}</h6>
                            <Link to="/profile/edit" className="btn btn-success me-2">Edit Profile</Link>
                            <button onClick={handleLogout} className="btn btn-danger">Logout</button>
                        </div>
                    </div>
                </div>
                <div className="col-md-8">
                    <h2>Posts:</h2>
                    {posts.map(post => (
                        <Post key={post._id} post={post} />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default UserProfile;
