import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import avatar from '../assets/user.png';
const OtherUsers = () => {
    const { id } = useParams(); // Get user id from URL
  const [user, setUser] = useState({});
  const [isFollowing, setIsFollowing] = useState(false); // Track follow status

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${id}`);
        setUser(response.data);
        // You may also want to check if the logged-in user is following this user
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    fetchUser();
  }, [id]);

  const handleFollow = async () => {
    try {
      await axios.post(`http://localhost:5000/api/users/follow/${id}`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setIsFollowing(true);
    } catch (error) {
      console.error('Error following user:', error);
    }
  };

  return (
        <div className="container">
            <div className="user-profile">
                <div className="profile-header-background">
                    <img src="http://demo.thedevelovers.com/dashboard/queenadmin-1.2/assets/img/city.jpg" alt="Profile Header Background" />
                </div>
                <div className="row">
                    <div className="col-md-4">
                        <div className="profile-info-left">
                            <div className="text-center">
                                <img
                                    src={user.profilePicture || avatar}
                                    alt={user.username}
                                    className="avatar img-circle"
                                />
                                <h2>{user.username}</h2>
                            </div>
                            <div className="action-buttons">
                                <div className="row">
                                    <div className="col-xs-6">
                                        <button onClick={handleFollow} className="btn btn-success btn-block">
                                            {isFollowing ? 'Following' : 'Follow'}
                                        </button>
                                    </div>
                                    <div className="col-xs-6">
                                        <button className="btn btn-primary btn-block"><i className="fa fa-android-mail"></i> Message</button>
                                    </div>
                                </div>
                            </div>
                            <div className="section">
                                <h3>About Me</h3>
                                <p>{user.bio || 'No bio available'}</p>
                            </div>
                            <div className="section">
                                <h3>Statistics</h3>
                                <p><span className="badge">{user.following?.length || 0}</span> Following</p>
                                <p><span className="badge">{user.followers?.length || 0}</span> Followers</p>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-8">
                        <div className="profile-info-right">
                            <h4>User Posts</h4>
                            {/* Check if user.posts exists and map through them */}
                            {user.posts && user.posts.length > 0 ? (
                                user.posts.map((post) => (
                                    <div key={post._id} className="media post-item">
                                        <a href="#" className="pull-left">
                                            <img src={post.user.profilePicture || avatar} alt="Avatar" className="media-object avatar" />
                                        </a>
                                        <div className="media-body">
                                            <h5 className="post-title">{post.title || 'Post Title'}</h5> {/* Replace with appropriate post field */}
                                            <p>{post.content || 'Post content goes here...'}</p> {/* Replace with appropriate post field */}
                                            <small className="text-muted">{new Date(post.createdAt).toLocaleString()}</small> {/* Format date */}
                                        </div>
                                    </div>
                                ))
                            ) : (
                                <p>No posts available.</p>
                            )}
                            <button type="button" className="btn btn-default center-block">
                                <i className="fa fa-refresh"></i> Load more posts
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default OtherUsers;
