import React, { useState } from 'react';
import axios from 'axios';
import '../style.css'; // Import custom CSS styles

const Post = ({ post, fetchPosts }) => {
    const [comment, setComment] = useState(''); // State for comment input

    const handleLike = async () => {
        try {
            await axios.post(`http://localhost:5000/api/posts/${post._id}/like`, {}, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Post liked');
            fetchPosts(); // Refresh the post list after liking
        } catch (error) {
            console.error('Error liking post:', error);
        }
    };

    const handleComment = async (e) => {
        e.preventDefault(); // Prevent form submission
        if (!comment.trim()) return; // Prevent submitting empty comments
        try {
            await axios.post(`http://localhost:5000/api/posts/${post._id}/comment`, { content: comment }, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Comment added');
            fetchPosts(); // Refresh the post list after adding a comment
            setComment(''); // Clear the comment input after submission
        } catch (error) {
            console.error('Error adding comment:', error);
        }
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleString();
    };

    return (
        <div className="card mb-3 shadow-none border">
            <div className="card-body border-bottom">
                {/* Post Header with Avatar, Name, and Time */}
                <div className="d-flex align-items-center gap-3">
                    <img src="https://bootdey.com/img/Content/avatar/avatar1.png" alt="avatar" className="rounded-circle" width="40" height="40" />
                    <h6 className="fw-semibold mb-0 fs-4">Mathew Anderson</h6>
                    <span className="fs-6 ms-auto">15 min ago</span>
                </div>

                {/* Post Content */}
                <p className="text-dark my-3">{post.content}</p>
                {post.image && (
                    <img src={post.image} alt="Post" className="img-fluid rounded-4 w-100 object-fit-cover" style={{ height: '360px' }} />
                )}

                {/* Post Actions (Like, Comment, Share) */}
                <div className="d-flex align-items-center my-3">
                    <div className="d-flex align-items-center gap-2">
                        <a className="text-white d-flex align-items-center justify-content-center bg-primary p-2 fs-4 rounded-circle" href="#!" onClick={handleLike}>
                            <i className="fa fa-thumbs-up"></i>
                        </a>
                        <span className="text-dark fw-semibold">{post.likes.length}</span>
                    </div>
                    <div className="d-flex align-items-center gap-2 ms-4">
                        <a className="text-white d-flex align-items-center justify-content-center bg-secondary p-2 fs-4 rounded-circle" href="#!">
                            <i className="fa fa-comments"></i>
                        </a>
                        <span className="text-dark fw-semibold">{post.comments.length}</span>
                    </div>
                    <a className="text-dark ms-auto d-flex align-items-center justify-content-center bg-transparent p-2 fs-4 rounded-circle" href="#!">
                        <i className="fa fa-share"></i>
                    </a>
                </div>

                {/* Comments Section */}
                <div className="position-relative">
                    {post.comments.map((comment) => (
                        <div key={comment._id} className="p-4 rounded-2 bg-light mb-3">
                            <div className="d-flex align-items-center gap-3">
                                <img src="https://bootdey.com/img/Content/avatar/avatar3.png" alt="comment avatar" className="rounded-circle" width="33" height="33" />
                                {/* Display the actual comment author and timestamp */}
                                <h6 className="fw-semibold mb-0 fs-4">{comment.user.name}</h6>
                                <span className="fs-6">{formatDate(comment.createdAt)}</span>
                            </div>
                            <p className="my-3">{comment.content}</p>
                            <div className="d-flex align-items-center gap-2">
                                <a className="text-white d-flex align-items-center justify-content-center bg-primary p-2 fs-4 rounded-circle" href="#!">
                                    <i className="fa fa-thumbs-up"></i>
                                </a>
                                <span className="text-dark fw-semibold">55</span>
                                <a className="text-white d-flex align-items-center justify-content-center bg-secondary p-2 fs-4 rounded-circle ms-4" href="#!">
                                    <i className="fa fa-arrow-up"></i>
                                </a>
                            </div>
                        </div>
                    ))}

                    {/* Comment Input */}
                    <div className="d-flex align-items-center gap-3 p-3">
                        <img src="https://bootdey.com/img/Content/avatar/avatar1.png" alt="avatar" className="rounded-circle" width="33" height="33" />
                        <input
                            type="text"
                            className="form-control py-2"
                            placeholder="Comment"
                            value={comment}
                            onChange={(e) => setComment(e.target.value)}
                        />
                        <button className="btn btn-primary" onClick={handleComment}>Comment</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Post;
