// src/components/UserProfile.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Post from './Post';

const UserProfile = () => {
    const [user, setUser] = useState(null);
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                // Fetch user profile
                const response = await axios.get('http://localhost:5000/api/users/profile', {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });
                console.log('User Profile Response:', response.data); // Log the response to see what is being returned
                setUser(response.data);
    
                // Fetch user's posts if user._id exists
                if (response.data._id) {
                    const postsResponse = await axios.get(`http://localhost:5000/api/posts/user/${response.data._id}`, {
                        headers: {
                            'Authorization': `Bearer ${localStorage.getItem('token')}`
                        }
                    });
                    setPosts(postsResponse.data);
                } else {
                    console.error('User ID is undefined');
                }
            } catch (error) {
                console.error('Error fetching user profile:', error);
            }
        };
    
        fetchUserProfile();
    }, []);

    if (!user) return <p>Loading...</p>;

    return (
        <div>
            <h1>{user.username}'s Profile</h1>
            <img src={user.profilePicture} alt="Profile" />
            <p>{user.bio}</p>
            <h3>Followers: {user.followers ? user.followers.length : 0}</h3>
            <h3>Following: {user.following ? user.following.length : 0}</h3>
            <Link to="/profile/edit" style={{ padding: '10px', backgroundColor: '#28a745', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>Edit Profile</Link>
            <h2>Posts:</h2>
            {posts.map(post => (
                <Post key={post._id} post={post} />
            ))}
        </div>
    );
};

export default UserProfile;
