import React from 'react';
import { useNavigate } from 'react-router-dom';
import PostForm from '../components/PostForm.js';
import PostList from '../components/PostList.js';

const Home = () => {
  const navigate = useNavigate();

  // Check if token is in localStorage
  const token = localStorage.getItem('token');
  if (!token) {
    navigate('/login'); // Redirect to login if not authenticated
  }

  return (
    <div>
    <h1>Social Media App</h1>
    <div style={{ marginBottom: '20px' }}>
    <Link to="/profile" style={{ marginRight: '10px', padding: '10px', backgroundColor: '#007bff', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>View Profile</Link>
    <Link to="/profile/edit" style={{ padding: '10px', backgroundColor: '#28a745', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>Edit Profile</Link>
</div>
    <PostList />
</div>
  );
};

export default Home;
