import React, { useState } from 'react';
import axios from 'axios';

const PostForm = ({ fetchPosts }) => { // Accept fetchPosts as a prop
    const [content, setContent] = useState('');
    const [image, setImage] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('content', content);
        if (image) {
            formData.append('image', image);
        }

        try {
            const response = await axios.post('http://localhost:5000/api/posts/create', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Post created:', response.data);
            fetchPosts(); // Call fetchPosts to refresh the post list
            setContent(''); // Clear the input
            setImage(null); // Clear the image input
        } catch (error) {
            console.error('Error creating post:', error);
        }
    };

    return (
        <div className="col-lg-8">
            <div className="card shadow-none border">
                <div className="card-body">
                    <form onSubmit={handleSubmit}>
                        <div className="form-floating mb-3">
                            <textarea
                                className="form-control"
                                placeholder="Leave a comment here"
                                id="floatingTextarea2"
                                style={{ height: '137px' }}
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                required
                            ></textarea>
                            <label htmlFor="floatingTextarea2" className="p-7">Share your thoughts</label>
                        </div>
                        <div className="d-flex align-items-center gap-2">
                           
                            <a className="text-white d-flex align-items-center justify-content-center bg-primary p-2 fs-4 rounded-circle" href="javascript:void(0)">
                                <i className="fa fa-photo"></i>
                            </a>
                            <a href="javascript:void(0)" className="text-dark px-3 py-2">
                                 <input type="file" onChange={(e) => setImage(e.target.files[0])} />Photo / Video</a>
                            <a href="javascript:void(0)" className="d-flex align-items-center gap-2">
                            </a>
                            <button className="btn btn-primary ms-auto" type="submit">Post</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default PostForm;
