import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Post from './Post';
import PostForm from './PostForm'; // Import PostForm
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../style.css'; 
import avatar from '../assets/user.png';

const UserProfile = () => {
    const [profile, setProfile] = useState(null);
    const [posts, setPosts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/users/profile', {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });
                setProfile(response.data);

                if (response.data._id) {
                    const postsResponse = await axios.get(`http://localhost:5000/api/posts/user/${response.data._id}`, {
                        headers: {
                            'Authorization': `Bearer ${localStorage.getItem('token')}`
                        }
                    });
                    setPosts(postsResponse.data);
                }
            } catch (error) {
                console.error('Error fetching user profile:', error);
            }
        };

        fetchUserProfile();
    }, []);

    const fetchPosts = async () => {
        if (profile && profile._id) {
            const postsResponse = await axios.get(`http://localhost:5000/api/posts/user/${profile._id}`, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            setPosts(postsResponse.data);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    if (!profile) return <p>Loading...</p>;

    return (
        <div className="container">
            {/* Profile Header */}
            <div className="card overflow-hidden">
                {/* Profile content */}
            </div>

            <div className="tab-content">
                <div className="tab-pane fade show active">
                    <div className="row">
                        <div className="col-lg-4">
                            {/* User Bio and Info */}
                        </div>

                        <div className="col-lg-8">
                            {/* Add Post Form */}
                            <PostForm fetchPosts={fetchPosts} />  {/* Include the post form */}
                            <div className="row">
                                {posts.map(post => (
                                    <div className="col-md-12 mb-3" key={post._id}>
                                        <Post post={post} />
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UserProfile;
