import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Post from './Post';

const UserProfile = () => {
  const [user, setUser] = useState(null);  // Replace with actual user data
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users/profile', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, []);

  return user ? (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-4">
          <div className="card">
            <div className="card-body">
              <div className="text-center">
                <img src={user.profilePicture} alt={user.username} className="img-fluid profile-pic rounded-circle" />
                <h5 className="mt-3">{user.username}</h5>
                <p>{user.bio}</p>
              </div>
              <div className="follow-info mt-4 text-center">
                <span className="mr-3">
                  Followers <strong>{user.followers.length}</strong>
                </span>
                <span>
                  Following <strong>{user.following.length}</strong>
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Posts Section */}
      <div className="row">
        {posts.map((post) => (
          <div className="col-lg-6 mb-4" key={post._id}>
            <Post post={post} />
          </div>
        ))}
      </div>
    </div>
  ) : (
    <p>Loading...</p>
  );
};

export default UserProfile;
