// src/components/PostList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PostList = () => {
    const [posts, setPosts] = useState([]);

    const fetchPosts = async () => {
        try {
            const response = await axios.get('/api/posts'); // Create a route to get posts
            setPosts(response.data);
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };
    const [commentContent, setCommentContent] = useState('');

const handleComment = async (postId) => {
    try {
        const response = await axios.post(`/api/posts/comment/${postId}`, { content: commentContent }, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        });
        console.log('Comment added:', response.data);
        fetchPosts(); // Refresh the posts after commenting
    } catch (error) {
        console.error('Error adding comment:', error);
    }
};
    const handleLike = async (postId) => {
        try {
            const response = await axios.put(`/api/posts/like/${postId}`, {}, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Post liked:', response.data);
            fetchPosts(); // Refresh the posts after liking
        } catch (error) {
            console.error('Error liking post:', error);
        }
    };
    useEffect(() => {
        fetchPosts();
    }, []);

    return (
        <div>
            {posts.map(post => (
                <div key={post._id}>
                    <h4>{post.user.name}</h4>
                    <p>{post.content}</p>
                    {post.image && <img src={post.image} alt="Post" />}
                    {<button onClick={() => handleLike(post._id)}>Like</button>}
                </div>
            ))}
        </div>
    );
};

export default PostList;
