import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import Post from '../components/Post';  // Import Post component
import '../style.css'; // Import the CSS

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users/profile', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        setUser(response.data);
        if (response.data._id) {
          const postsResponse = await axios.get(`http://localhost:5000/api/posts/user/${response.data._id}`, {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
          });
          setPosts(postsResponse.data);
        }
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };

    fetchUserProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div className="card overflow-hidden">
    <div className="card-body p-0">
      <div className="d-flex align-items-center p-4">
        <img src={user.profilePicture} className="rounded-circle" alt="Profile" />
        <div className="ms-7">
          <h3>{user.name}</h3>
          <p>{user.bio}</p>
        </div>
      </div>
    </div>
  </div>
  );
};

export default UserProfile;
