import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import PostList from '../components/PostList.js';
import '../style.css'; // Import the CSS
import avatar from '../assets/user-circle.svg';
import axios from 'axios';

const Home = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [loggedInUser, setLoggedInUser] = useState(null); // To store logged-in user info

  const token = localStorage.getItem('token');
  if (!token) {
    navigate('/login');
  }

  // Fetch the logged-in user's data
  useEffect(() => {
    const fetchLoggedInUser = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setLoggedInUser(response.data); // Assuming the logged-in user data is returned
      } catch (error) {
        console.error('Error fetching logged-in user:', error);
      }
    };
    fetchLoggedInUser();
  }, [token]);

  // Fetch all users excluding the logged-in user
  useEffect(() => {
    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/users/users', {
                headers: { Authorization: `Bearer ${token}` } // Include the token here
            });
            const allUsers = response.data;
            // Filter out the logged-in user from the users list
            if (loggedInUser) {
                const filteredUsers = allUsers.filter(user => user._id !== loggedInUser._id);
                setUsers(filteredUsers);
            } else {
                setUsers(allUsers);
            }
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    fetchUsers();
}, [loggedInUser, token]); 

  return (
    <div className="container-users">
      {/* Button to navigate to the current logged-in user's profile */}
      {loggedInUser && (
        <div style={{ marginBottom: '20px' }}>
          <Link to={`/user/${loggedInUser._id}`} className="btn btn-primary">
            View My Profile
          </Link>
        </div>
      )}
      
      <div className="user-list">
        <h4 className="fw-semibold mb-3">Users</h4>
        <div className="card shadow-none border">
          <div className="card-body">
            <div className="row">
              {users.map((user) => (
                <div className="col-12 mb-4" key={user._id}>
                  <div className="text-center">
                    <Link to={`/user/${user._id}`}>
                      <img
                        src={user.profilePicture || avatar}
                        alt={user.username}
                        className="rounded-2 img-fluid mb-2"
                        style={{ width: '100px', height: '100px' }}
                      />
                      <p className="fw-semibold">{user.username}</p>
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
  
      <div className="posts-section">
        <PostList />
      </div>
    </div>
  );
};

export default Home;
