import React from 'react';
import { useNavigate } from 'react-router-dom';
import PostList from '../components/PostList.js';
import { Link } from 'react-router-dom';
import '../style.css'; // Import the CSS

import axios from 'axios';
const Home = () => {
  const navigate = useNavigate();

  const token = localStorage.getItem('token');
  if (!token) {
    navigate('/login');
  }
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users');
        setUsers(response.data); // Assuming response.data is an array of users
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);
  return (
    <div className="container">
      <h1>Social Media App</h1>
      <div style={{ marginBottom: '20px' }}>
        <Link to="/profile" className="link">View Profile</Link>
      </div>
      <div className="card shadow-none border">
      <div className="card-body">
        <h4 className="fw-semibold mb-3">Users</h4>
        <div className="row">
          {users.map((user) => (
            <div className="col-4 mb-4" key={user._id}>
              <div className="text-center">
                <img
                  src={user.profilePicture || 'https://bootdey.com/img/Content/avatar/avatar1.png'}
                  alt={user.username}
                  className="rounded-2 img-fluid mb-2"
                  style={{ width: '100px', height: '100px' }} // Adjust size as needed
                />
                <p className="fw-semibold">{user.username}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
      <PostList />
    </div>
  );
};

export default Home;
