import React, { useState } from 'react';
import axios from 'axios';
import '../style.css'; // Import CSS styles

const Post = ({ post, fetchPosts }) => {
    const [comment, setComment] = useState(''); // State for comment input

    const handleLike = async () => {
        try {
            await axios.post(`http://localhost:5000/api/posts/${post._id}/like`, {}, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Post liked');
            fetchPosts(); // Refresh the post list after liking
        } catch (error) {
            console.error('Error liking post:', error);
        }
    };

    const handleComment = async (e) => {
        e.preventDefault(); // Prevent form submission
        if (!comment.trim()) return; // Prevent submitting empty comments
        try {
            await axios.post(`http://localhost:5000/api/posts/${post._id}/comment`, { content: comment }, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Comment added');
            fetchPosts(); // Refresh the post list after adding a comment
            setComment(''); // Clear the comment input after submission
        } catch (error) {
            console.error('Error adding comment:', error);
        }
    };

    return (
        <div className="col-lg-8 mb-3">
            <div className="card shadow-none border">
                <div className="card-body">
                    <h5 className="card-title">{post.content}</h5>
                    {post.image && <img src={post.image} alt="Post" className="img-fluid mb-2" />} {/* Post image */}
                    
                    <div className="d-flex align-items-center">
                        <button onClick={handleLike} className="btn btn-outline-primary me-2">
                            Like
                        </button>
                        <span>{post.likes.length} Likes</span> {/* Display number of likes */}
                    </div>

                    {/* Comment Form */}
                    <form className="mt-3" onSubmit={handleComment}>
                        <div className="form-floating mb-3">
                            <textarea 
                                className="form-control" 
                                placeholder="Leave a comment here" 
                                id="floatingTextarea2" 
                                style={{ height: '50px' }}
                                value={comment}
                                onChange={(e) => setComment(e.target.value)}
                            />
                            <label htmlFor="floatingTextarea2" className="p-7">Comment</label>
                        </div>
                    </form>

                    {/* Display Comments */}
                    <div className="comments-list mt-3">
                        {post.comments.map(comment => (
                            <div key={comment._id} className="comment mb-2">
                                {comment.content}
                            </div> // Ensure a unique key here
                        ))}
                        <span>{post.comments.length} Comments</span> {/* Display number of comments */}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Post;
