import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import avatar from '../assets/user.png';
import './UserProfile.css';

const OtherUsers = () => {
  const { id } = useParams();
  const [user, setUser] = useState({});
  const [posts, setPosts] = useState([]); // State to hold user posts
  const [isFollowing, setIsFollowing] = useState(false);

  // Fetch user data
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${id}`);
        setUser(response.data);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    fetchUser();
  }, [id]);

  // Fetch user posts
  useEffect(() => {
    const fetchPostsByUser = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/posts/user/${id}`);
        setPosts(response.data);  // Set the posts state with the fetched data
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPostsByUser();
  }, [id]);

  const handleFollow = async () => {
    try {
      await axios.post(
        `http://localhost:5000/api/users/follow/${id}`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setIsFollowing(true);
    } catch (error) {
      console.error('Error following user:', error);
    }
  };

  return (
    <div className="container">
      <div className="user-profile">
        <div className="profile-header-background">
          <img
            src="http://demo.thedevelovers.com/dashboard/queenadmin-1.2/assets/img/city.jpg"
            alt="Profile Header Background"
          />
        </div>
        <div className="row">
          <div className="col-md-4">
            <div className="profile-info-left">
              <div className="text-center">
                <img
                  src={user.profilePicture || avatar}
                  alt={user.username}
                  className="avatar img-circle"
                />
                <h2>{user.username}</h2>
              </div>
              <div className="action-buttons">
                <div className="row">
                  <div className="col-xs-6">
                    <button
                      onClick={handleFollow}
                      className="btn btn-success btn-block"
                    >
                      {isFollowing ? 'Following' : 'Follow'}
                    </button>
                  </div>
                  <div className="col-xs-6">
                    <a href="#" className="btn btn-primary btn-block">
                      Message
                    </a>
                  </div>
                </div>
              </div>
              <div className="section">
                <h3>About Me</h3>
                <p>{user.bio || 'No bio available'}</p>
              </div>
              <div className="section">
                <h3>Statistics</h3>
                <p><span className="badge">{user.following?.length || 0}</span> Following</p>
                <p><span className="badge">{user.followers?.length || 0}</span> Followers</p>
              </div>
            </div>
          </div>
          <div className="col-md-8">
            <div className="profile-info-right">
              <ul className="nav nav-pills nav-pills-custom-minimal custom-minimal-bottom">
                <li className="active"><a href="#activities" data-toggle="tab">ACTIVITIES</a></li>
              </ul>
              <div className="tab-content">
                <div className="tab-pane fade in active" id="activities">
                  {posts && posts.length > 0 ? (
                    posts.map((post) => (
                      <div key={post._id} className="media activity-item">
                        <a href="#" className="pull-left">
                          <img
                            src={post.user.profilePicture || avatar}
                            alt="Avatar"
                            className="media-object avatar"
                          />
                        </a>
                        <div className="media-body">
                          <p className="activity-title">
                            <a href="#">{user.username}</a> posted: {post.content}
                            <small className="text-muted">
                              - {new Date(post.createdAt).toLocaleString()}
                            </small>
                          </p>
                          {post.image && <img src={post.image} alt="Post image" />}
                          <p>{post.likes.length} Likes</p>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p>No posts available.</p>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OtherUsers;
