// src/components/Post.js
import React from 'react';
import axios from 'axios';

const Post = ({ post }) => {
    const handleLike = async () => {
        try {
            await axios.post(`http://localhost:5000/api/posts/${post._id}/like`, {}, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Post liked');
            // Optionally refresh the post data here
        } catch (error) {
            console.error('Error liking post:', error);
        }
    };

    const handleComment = async (comment) => {
        try {
            await axios.post(`http://localhost:5000/api/posts/${post._id}/comment`, { content: comment }, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Comment added');
            // Optionally refresh the post data here
        } catch (error) {
            console.error('Error adding comment:', error);
        }
    };

    return (
        <div>
            <h3>{post.content}</h3>
            {post.image && <img src={post.image} alt="Post" />}
            <button onClick={handleLike}>Like</button>
            <div>
                <input type="text" placeholder="Add a comment..." onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                        handleComment(e.target.value);
                        e.target.value = ''; // Clear input after submit
                    }
                }} />
            </div>
            <div>
                {post.comments.map(comment => (
                    <div key={comment._id}>{comment.content}</div> // Ensure a unique key here
                ))}
            </div>
        </div>
    );
};

export default Post;
