import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Post from './Post';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const UserProfile = () => {
    const [user, setUser] = useState(null);
    const [posts, setPosts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/users/profile', {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });
                setUser(response.data);
    
                if (response.data._id) {
                    const postsResponse = await axios.get(`http://localhost:5000/api/posts/user/${response.data._id}`, {
                        headers: {
                            'Authorization': `Bearer ${localStorage.getItem('token')}`
                        }
                    });
                    setPosts(postsResponse.data);
                } else {
                    console.error('User ID is undefined');
                }
            } catch (error) {
                console.error('Error fetching user profile:', error);
            }
        };
    
        fetchUserProfile();
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    if (!user) return React.createElement('p', null, 'Loading...');

    return React.createElement(
        'div',
        { style: { backgroundColor: '#E9EFEC', padding: '20px', minHeight: '100vh' } },
        React.createElement('h1', { style: { color: '#16423C' } }, `${user.username}'s Profile`),
        React.createElement('img', {
            src: user.profilePicture,
            alt: 'Profile',
            style: { borderRadius: '50%', border: '3px solid #16423C' }
        }),
        React.createElement('p', { style: { color: '#6A9C89' } }, user.bio),
        React.createElement('div', null,
            React.createElement('h3', { style: { color: '#6A9C89' } }, `Followers: ${user.followers ? user.followers.length : 0}`),
            React.createElement('h3', { style: { color: '#6A9C89' } }, `Following: ${user.following ? user.following.length : 0}`)
        ),
        React.createElement('div', null,
            React.createElement(Link, {
                to: "/profile/edit",
                style: { padding: '10px', backgroundColor: '#28a745', color: 'white', textDecoration: 'none', borderRadius: '5px' }
            }, 'Edit Profile'),
            React.createElement('button', {
                onClick: handleLogout,
                style: { padding: '10px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', marginLeft: '10px' }
            }, 'Logout')
        ),
        React.createElement('h2', { style: { color: '#16423C' } }, 'Posts:'),
        ...posts.map(post => React.createElement(Post, { key: post._id, post }))
    );
};

export default UserProfile;
