import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import Post from '../components/Post';  // Import Post component
import '../styles.css'; // Import the CSS

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users/profile', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        setUser(response.data);
        if (response.data._id) {
          const postsResponse = await axios.get(`http://localhost:5000/api/posts/user/${response.data._id}`, {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
          });
          setPosts(postsResponse.data);
        }
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };

    fetchUserProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div className="container">
      <h1>{user.username}'s Profile</h1>
      <img src={user.profilePicture} alt="Profile" className="profile-picture" />
      <p>{user.bio}</p>
      <h3>Followers: {user.followers ? user.followers.length : 0}</h3>
      <h3>Following: {user.following ? user.following.length : 0}</h3>
      <Link to="/profile/edit" className="link">Edit Profile</Link>
      <button onClick={handleLogout} className="button logout-button">Logout</button>
      <h2>Posts:</h2>
      {posts.map(post => (
        <Post key={post._id} post={post} />  
      ))}
    </div>
  );
};

export default UserProfile;
